mapper.insert:
insert into mall_member_point_history (
    member_id, amount, points, note, platform, year,
    create_by, create_time, update_by, update_time, remark
) values (
    #{memberId}, #{amount}, #{points}, #{note}, #{platform}, #{year},
    #{createBy}, NOW(), #{updateBy}, NOW(), #{remark}
)

mapper.select:
select count(1) from mall_member_point_history
where member_id = #{memberId}
and note like CONCAT('%Order payment: ', #{orderSn}, '%')

select COALESCE(SUM(points), 0) from mall_member_point_history
where member_id = #{memberId}
and year = #{year}
